import { useRouter } from 'next/router'
import Layout from '../components/layout';

const Post = (props) => {
    const router = useRouter();

    return ( 
        <Layout>
            <h1>{router.query.title}</h1>
            <p>This is post content</p>
        </Layout>
     );
}
 
export default Post;
